mapper.select:
SELECT DISTINCT defKey, processDefName, version, act1.processCategory AS processCategory FROM ( SELECT ard.KEY_ AS defKey, ard.NAME_ AS processDefName, ard.VERSION_ AS version, ard.CATEGORY_ AS processCategory FROM ( SELECT arp.KEY_, arp.NAME_, arp.VERSION_, arp.ID_, arp.CATEGORY_ FROM act_re_procdef arp ORDER BY arp.VERSION_ DESC ) ard GROUP BY ard.key_ ) AS act1, act_process_type apt WHERE state_ = 0
and act1.processDefName LIKE CONCAT('%','${processDefName}','%')
and act1.processCategory = #{processCategory ,jdbcType=VARCHAR}
