mapper.select:
SELECT taskId, startTime, processInstanceId, taskName, dueDate, taskKey, priority, systemId, taskType, processDefId, KEY_ as processKey, state, arp.NAME_ as processDefName, arp.CATEGORY_ as processCategory, ahp.NAME_ as processInstanceName, ahp.START_TIME_ as applyTime FROM ( SELECT t.TASK_ID_ taskId, a.CREATE_TIME_ startTime, a.PROC_INST_ID_ processInstanceId, a.NAME_ taskName, a.DUE_DATE_ AS dueDate, a.TASK_DEF_KEY_ taskKey, a.PRIORITY_ AS priority, a.TENANT_ID_ as systemId, (SELECT '1' FROM DUAL) AS taskType, a.PROC_DEF_ID_ processDefId, IFNULL(a.SUSPENSION_STATE_, NULL) AS state FROM ( SELECT art.ID_ AS TASK_ID_, art.ASSIGNEE_ AS USER_ID_ FROM act_ru_task art WHERE art.ID_ IS NOT NULL AND art.ASSIGNEE_ IS NOT NULL AND art.ASSIGNEE_ = #{handerId} AND ( art.FORM_KEY_ != '1' OR art.FORM_KEY_ IS NULL ) UNION SELECT TASK_ID_, USER_ID_ FROM ACT_RU_IDENTITYLINK I, ACT_RU_TASK T WHERE TASK_ID_ IS NOT NULL AND USER_ID_ IS NOT NULL AND I.TASK_ID_ = T.ID_ AND T.ASSIGNEE_ IS NULL AND TYPE_ = 'candidate' ) t, act_ru_task a WHERE t.TASK_ID_ = a.ID_ AND t.USER_ID_ = #{handerId} UNION SELECT aht.ID_ AS taskId, aht.START_TIME_ AS startTime, aht.PROC_INST_ID_ AS processInstanceId, aht.NAME_ AS taskName, aht.DUE_DATE_ AS dueDate, aht.TASK_DEF_KEY_ taskKey, aht.PRIORITY_ AS priority, aht.TENANT_ID_ as systemId, (SELECT '2' FROM DUAL) AS taskType, aht.PROC_DEF_ID_ AS processDefId, IFNULL( ( SELECT DISTINCT art.SUSPENSION_STATE_ FROM act_ru_task art WHERE art.PROC_INST_ID_ = aht.PROC_INST_ID_ ), 3 ) AS state FROM act_inform_process act, act_hi_taskinst aht WHERE aht.ID_ = act.task_id AND act.inform_person_id = #{handerId} ) AS new_run LEFT JOIN act_re_procdef arp ON new_run.processDefId = arp.ID_ LEFT JOIN act_hi_procinst ahp on ahp.PROC_INST_ID_ = new_run.processInstanceId
and arp.NAME_ like CONCAT('%','${processDefName}','%')
and ahp.NAME_ like CONCAT('%','${processInstanceName}','%')
and ahp.TENANT_ID_ = #{systemId,jdbcType=VARCHAR}
and new_run.state = #{state,jdbcType=INTEGER}
and new_run.priority = #{priority,jdbcType=INTEGER}
and arp.CATEGORY_ = #{processCategory,jdbcType=VARCHAR}
ORDER BY startTime DESC, state ASC

SELECT taskId, startTime, endTime, processInstanceId, taskName, priority, processDefName, startUserId, applyTime, stopTime, processCategory, processKey, processInstanceName, handerId, IFNULL(state, 3) AS state FROM ( SELECT aht.ID_ AS taskId, aht.START_TIME_ AS startTime, aht.END_TIME_ AS endTime, aht.PROC_INST_ID_ AS processInstanceId, aht.NAME_ AS taskName, aht.PRIORITY_ AS priority, arp.NAME_ AS processDefName, ahp.START_USER_ID_ AS startUserId, ahp.START_TIME_ AS applyTime, ahp.END_TIME_ AS stopTime, arp.CATEGORY_ as processCategory, arp.KEY_ as processKey, ahp.NAME_ as processInstanceName, aht.ASSIGNEE_ as handerId, ( SELECT DISTINCT act_ru_task.SUSPENSION_STATE_ FROM act_ru_task WHERE act_ru_task.PROC_INST_ID_ = aht.PROC_INST_ID_ ) AS state FROM act_hi_taskinst aht LEFT JOIN act_re_procdef arp ON aht.PROC_DEF_ID_ = arp.ID_ LEFT JOIN act_hi_procinst ahp ON ahp.PROC_INST_ID_ = aht.PROC_INST_ID_ WHERE ( aht.FORM_KEY_ != '1' OR aht.FORM_KEY_ IS NULL ) and aht.END_TIME_ is not null and ahp.TENANT_ID_ = #{systemId} ) AS totalProcessInstance
and processInstanceId like CONCAT('%','${processInstanceId}','%')
and processDefName like CONCAT('%','${processDefName}','%')
and processInstanceName like CONCAT('%','${processInstanceName}','%')
and applyTime >= #{applyTime}
and applyTime <= #{stopTime}
and handerId = #{handerId}
and priority = #{priority}
and processCategory = #{processCategory,jdbcType=INTEGER}
and state = #{state,jdbcType=INTEGER}
and state IS NULL
ORDER BY state ASC, applyTime DESC

SELECT * FROM ( SELECT taskId, startTime, endTime, processInstanceId, taskName, priority, processDefName, startUserId, applyTime, stopTime, processCategory, processKey, processInstanceName, handerId, IFNULL(state, 3) AS state FROM ( SELECT aht.ID_ AS taskId, aht.START_TIME_ AS startTime, aht.END_TIME_ AS endTime, aht.PROC_INST_ID_ AS processInstanceId, aht.NAME_ AS taskName, aht.PRIORITY_ AS priority, arp.NAME_ AS processDefName, ahp.START_USER_ID_ AS startUserId, ahp.START_TIME_ AS applyTime, ahp.END_TIME_ AS stopTime, arp.CATEGORY_ AS processCategory, arp.KEY_ AS processKey, ahp.NAME_ AS processInstanceName, aht.ASSIGNEE_ AS handerId, ( SELECT DISTINCT act_ru_task.SUSPENSION_STATE_ FROM act_ru_task WHERE act_ru_task.PROC_INST_ID_ = aht.PROC_INST_ID_ ) AS state FROM act_hi_taskinst aht LEFT JOIN act_re_procdef arp ON aht.PROC_DEF_ID_ = arp.ID_ LEFT JOIN act_hi_procinst ahp ON ahp.PROC_INST_ID_ = aht.PROC_INST_ID_ WHERE ( aht.FORM_KEY_ != '1' OR aht.FORM_KEY_ IS NULL ) AND aht.END_TIME_ IS NOT NULL AND ahp.TENANT_ID_ = #{systemId} ) AS totalProcessInstance WHERE handerId = #{handerId} ORDER BY endTime DESC ) AS a
and processInstanceId like CONCAT('%','${processInstanceId}','%')
and processDefName like CONCAT('%','${processDefName}','%')
and processInstanceName like CONCAT('%','${processInstanceName}','%')
and applyTime >= #{applyTime}
and applyTime <= #{stopTime}
and handerId = #{handerId}
and priority = #{priority}
and processCategory = #{processCategory,jdbcType=INTEGER}
and state = #{state,jdbcType=INTEGER}
GROUP BY processInstanceId ORDER BY state ASC, applyTime DESC
